import React, { useState } from 'react';
import Display from './Display';
import Keypad from './Keypad';
import './styles/App.css';

function App() {
  // Estado para almacenar la entrada actual del usuario
  const [input, setInput] = useState('');
  const [backgroundColor, setBackgroundColor] = useState('#f0f0f0'); // Color de fondo inicial
  const [buttonColor, setButtonColor] = useState('#d0d0d0'); // Color de botón inicial

  // Función que maneja los clics en los botones
  const handleClick = (value) => {
    if (value === 'C') {
      // Reinicia la entrada si se presiona 'C' (Clear)
      setInput('');
    } else if (value === '=') {
      try {
        // Evalúa la expresión ingresada y actualiza la entrada con el resultado
        setInput(eval(input).toString());
      } catch (error) {
        // Muestra 'Error' si la evaluación falla
        setInput('Error');
      }
    } else {
      // Agrega el valor del botón presionado a la entrada actual
      setInput(input + value);
    }
  };

  // Función para cambiar los colores
  const handleColorChange = () => {
    setBackgroundColor(prevColor => (prevColor === '#f0f0f0' ? '#d3d3d3' : '#f0f0f0'));
    setButtonColor(prevColor => (prevColor === '#d0d0d0' ? '#894432' : '#d0d0d0'));
  };

  return (
    <div className="calculator" style={{ backgroundColor }}>
      {/* Botón para cambiar el color */}
      <button className="color-toggle-button" onClick={handleColorChange} style={{ backgroundColor: buttonColor }}>
        Cambiar Color
      </button>
      {/* Componente que muestra la entrada actual */}
      <Display input={input} />
      {/* Componente que contiene los botones de la calculadora */}
      <Keypad handleClick={handleClick} />
    </div>
  );
}

export default App;
