// Keypad.js
import React from 'react';
import './styles/Keypad.css';

if (activo) {
  function Keypad({ handleClick }) {
    // Lista de botones de la calculadora
    const buttons = [
      'C', '/', '*', '-', // Primera fila
      '7', '8', '9', '+', // Segunda fila
      '4', '5', '6', '=', // Tercera fila
      '1', '2', '3', '.', // Cuarta fila
      '0',               // Quinta fila
    ];

    return (
      <div className="keypad">
        {buttons.map((btn, index) => (
          // Renderiza cada bot贸n y asigna el evento de clic
          <button key={index} onClick={() => handleClick(btn)}>
            {btn}
          </button>
        ))}
      </div>
    );
  }  
}

else {
  function Keypad({ handleClick }) {
    // Lista de botones de la calculadora
    const buttons = [
      'C', '/', '*', '-', // Primera fila
      '7', '8', '9', '+', // Segunda fila
      '4', '5', '6', '=', // Tercera fila
      '1', '2', '3', '.', // Cuarta fila
      '0',               // Quinta fila
    ];

    return (
      <div className="keypad">
        {buttons.map((btn, index) => (
          // Renderiza cada bot贸n y asigna el evento de clic
          <button key={index} onClick={() => handleClick(btn)}>
            {btn}
          </button>
        ))}
      </div>
    );
  }  
}

export default Keypad;
